import React from 'react';
import styled from 'styled-components';

const Hero = () => {
  return (
    <HeroSection>
      <h1>Hello, I'm [Your Name]</h1>
      <p>Welcome to my portfolio website. Explore my projects and get to know more about my work in VR and Fintech development.</p>
    </HeroSection>
  );
};

const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: url('/path-to-your-background-image.jpg') no-repeat center center/cover;
  color: #fff;
  text-align: center;
`;

export default Hero;
