import React, { useState } from 'react';

const ExperienceSection = () => {
  // Define your experience and education data
  const [selectedItem, setSelectedItem] = useState(null); // To track selected item

  // Sample data (replace with your own)
  const experienceData = [
    { id: 1, title: "Software Developer at Company A", summary: "Worked on frontend development using React and backend with Node.js." },
    { id: 2, title: "Intern at Company B", summary: "Assisted in developing mobile applications using React Native." },
    // Add more experiences as needed
  ];

  const educationData = [
    { id: 1, title: "Master's Degree in Computer Science", summary: "Completed with exceptional marks (Red diploma) at University XYZ." },
    { id: 2, title: "Bachelor's Degree in Software Engineering", summary: "Graduated with honors from University ABC." },
    // Add more education entries as needed
  ];

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="experience-section">
      <h2>Experience and Education</h2>
      <div className="experience-container">
        {/* Left side: List of items */}
        <div className="experience-list">
          <h3>Experience</h3>
          <ul>
            {experienceData.map(item => (
              <li key={item.id} onClick={() => handleItemClick(item)}>
                {item.title}
              </li>
            ))}
          </ul>
          <h3>Education</h3>
          <ul>
            {educationData.map(item => (
              <li key={item.id} onClick={() => handleItemClick(item)}>
                {item.title}
              </li>
            ))}
          </ul>
        </div>
        {/* Right side: Display selected item summary */}
        <div className="experience-summary">
          {selectedItem ? (
            <>
              <h3>{selectedItem.title}</h3>
              <p>{selectedItem.summary}</p>
            </>
          ) : (
            <p>Select an item to see details.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ExperienceSection;
