import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Header = () => {
  const [activeSection, setActiveSection] = useState('');

  // Function to determine which section is in view
  const handleScroll = () => {
    const heroSection = document.getElementById('hero');
    const projectsSection = document.getElementById('projects');
    const experienceSection = document.getElementById('experience');
    const contactSection = document.getElementById('contact');

    const scrollPosition = window.scrollY + 200; // Adjust the offset if needed

    if (
      scrollPosition < projectsSection.offsetTop
    ) {
      setActiveSection('hero');
    } else if (
      scrollPosition >= projectsSection.offsetTop &&
      scrollPosition < experienceSection.offsetTop
    ) {
      setActiveSection('projects');
    } else if (
      scrollPosition >= experienceSection.offsetTop &&
      scrollPosition < contactSection.offsetTop
    ) {
      setActiveSection('experience');
    } else if (scrollPosition >= contactSection.offsetTop) {
      setActiveSection('contact');
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Nav>
      <Logo href="#">Filip Veskrna</Logo>
      <NavMenu>
        <NavItem href="#about" active={activeSection === 'hero'}>
          About
        </NavItem>
        <NavItem href="#projects" active={activeSection === 'projects'}>
          Projects
        </NavItem>
        <NavItem href="#experience" active={activeSection === 'experience'}>
          Experience
        </NavItem>
        <NavItem href="#contact" active={activeSection === 'contact'}>
          Contact
        </NavItem>
        <ResumeButton href="/FilipVeskrnaCV.pdf" download>
          Download Resume
        </ResumeButton>
      </NavMenu>
    </Nav>
  );
};

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 0rem;
  color: #fff;
  position: sticky;
  top: 0;
  width: 100%;
  z-index: 1000;
  background-color: #333; /* Adjust background color as needed */
`;

const Logo = styled.a`
  font-size: 1.5rem;
  font-weight: bold;
  padding: 0rem 2rem;
  text-decoration: none;
  color: #fff;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  padding: 0rem 2rem;
`;

const NavItem = styled.a`
  margin-left: 2rem;
  color: ${(props) => (props.active ? '#e94560' : '#fff')};
  text-decoration: none;
  transition: color 0.3s;

  &:hover {
    color: #e94560;
  }
`;

const ResumeButton = styled.a`
  margin-left: 2rem;
  padding: 0.5rem 1rem;
  border: 2px solid #e94560;
  border-radius: 4px;
  color: #e94560;
  text-decoration: none;
  transition: background-color 0.3s, color 0.3s;

  &:hover {
    background-color: #e94560;
    color: #fff;
  }
`;

export default Header;
