// Header.js

import React from 'react';
import styled from 'styled-components';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();

  const isProjectDetailPage = location.pathname.startsWith('/project/');

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <Nav>
      <LogoContainer>
        <Link to="/" onClick={scrollToTop}>
          Filip Veskrna
        </Link>
      </LogoContainer>
      {!isProjectDetailPage && (
        <Menu>
          <NavItem>
            <a href="#projects">Projects</a>
          </NavItem>
          <NavItem>
            <a href="#experience">Experience</a>
          </NavItem>
          <NavItem>
            <a href="#contact">Contact</a>
          </NavItem>
          <DownloadButton href="/path-to-resume.pdf" download>
          Download Resume
        </DownloadButton>
        </Menu>
      )}
    </Nav>
  );
};

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  color: #fff;
  position: sticky;
  top: 0;
  z-index: 1000;
  background-color: #333; /* Adjust background color as needed */
`;

const LogoContainer = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
`;

const Menu = styled.ul`
  display: flex;
  list-style-type: none;
`;

const NavItem = styled.li`
  margin: 0 1rem;
`;

const DownloadButton = styled.a`
  background-color: #e94560; /* Button background color */
  color: #fff; /* Button text color */
  padding: 0.5rem 1rem;
  border-radius: 4px;
  text-decoration: none;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #c92a44; /* Darker shade of the button color on hover */
  }
`;

export default Header;
