import React from 'react';
import styled from 'styled-components';

const ContactForm = () => {
  return (
    <FormSection id="contact">
      <h2>Contact Me</h2>
      <form>
        <Label>
          Name:
          <Input type="text" name="name" />
        </Label>
        <Label>
          Email:
          <Input type="email" name="email" />
        </Label>
        <Label>
          Message:
          <TextArea name="message"></TextArea>
        </Label>
        <Button type="submit">Send</Button>
      </form>
    </FormSection>
  );
};

const FormSection = styled.section`
  height: 100vh;
  padding: 2rem;
  background: none;
  text-align: center;
`;

const Label = styled.label`
  display: block;
  margin: 1rem 0;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin-top: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 0.5rem;
  margin-top: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 0.5rem 2rem;
  margin-top: 1rem;
  border: none;
  border-radius: 4px;
  background: #e94560;
  color: #fff;
  cursor: pointer;
  &:hover {
    background: #d23a54;
  }
`;

export default ContactForm;
