import React from 'react';
import styled from 'styled-components';

const Hero = () => {
  return (
    <HeroSection>
      <HeroContent>
        <h1>Hello, I'm [Your Name]</h1>
        <p>Welcome to my portfolio website. Explore my projects and get to know more about my work in VR and Fintech development.</p>
      </HeroContent>
    </HeroSection>
  );
};

const HeroSection = styled.section`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: none;
  color: #fff;
  text-align: center;
`;

const HeroContent = styled.div`
  max-width: 600px;
`;

export default Hero;
