import React from 'react';
import styled from 'styled-components';
import { useParams, Link } from 'react-router-dom';

const Header = () => {
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <Nav>
      <Link to="/">Back to Home</Link>
      <NavMenu>
        <NavItem onClick={() => scrollToSection('projects')}>Projects</NavItem>
        <NavItem onClick={() => scrollToSection('ExperienceSection')}>Experience</NavItem>
        <NavItem onClick={() => scrollToSection('contact')}>Contact</NavItem>
        <DownloadButton href="/path-to-resume.pdf" download>
          Download Resume
        </DownloadButton>
      </NavMenu>
    </Nav>
  );
};

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  color: #fff;
  position: sticky;
  top: 0;
  z-index: 10000;
  background-color: #333; /* Adjust background color as needed */
`;

const LogoContainer = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  cursor: pointer;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
`;

const NavItem = styled.div`
  margin-left: 2rem; /* Adjust spacing between menu items */
  cursor: pointer;
`;

const DownloadButton = styled.a`
  background-color: #e94560; /* Button background color */
  color: #fff; /* Button text color */
  padding: 0.5rem 1rem;
  border-radius: 4px;
  text-decoration: none;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #c92a44; /* Darker shade of the button color on hover */
  }
`;

export default Header;
