import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Header = ({ showBackButton }) => {
  return (
    <Nav>
      <LogoContainer>
        {showBackButton ? (
          <StyledLink to="/">Filip Veskrna</StyledLink>
        ) : (
          <StyledSpan>Filip Veskrna</StyledSpan>
        )}
      </LogoContainer>
      <NavMenu>
        <NavItem>
          <StyledLink to="/projects">Projects</StyledLink>
        </NavItem>
        <NavItem>
          <StyledLink to="/experience">Experience</StyledLink>
        </NavItem>
        <NavItem>
          <StyledLink to="/contact">Contact</StyledLink>
        </NavItem>
        <NavItem>
          <DownloadButton href="/path-to-resume.pdf" download>
            Download Resume
          </DownloadButton>
        </NavItem>
      </NavMenu>
    </Nav>
  );
};

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 0rem;
  color: #fff;
  position: sticky;
  top: 0;
  width: 100%;
  z-index: 1000;
  background-color: #333; /* Adjust background color as needed */
`;

const LogoContainer = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  padding: 0rem 2rem;
  cursor: pointer;
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: #fff;

  &:hover {
    color: #ccc; /* Adjust hover color */
  }
`;

const StyledSpan = styled.span`
  cursor: pointer;
  color: #fff;

  &:hover {
    color: #ccc; /* Adjust hover color */
  }
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  padding: 0rem 2rem;
`;

const NavItem = styled.div`
  margin-left: 1.5rem; /* Adjust spacing between menu items */
`;

const DownloadButton = styled.a`
  background-color: #e94560; /* Button background color */
  color: #fff; /* Button text color */
  padding: 0.5rem 1rem;
  border-radius: 4px;
  text-decoration: none;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #c92a44; /* Darker shade of the button color on hover */
  }
`;

export default Header;
