// App.js

import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './Components/Header';
import Hero from './Components/Hero';
import Projects from './Components/Projects';
import ContactForm from './Components/ContactForm';
import ExperienceSection from './Components/ExperienceSection';
import ProjectDetail from './Components/ProjectDetail';

const App = () => {
  useEffect(() => {
    const handleScroll = (event) => {
      event.preventDefault();
      const sections = document.querySelectorAll('section');
      const scrollDistance = window.innerHeight;
      const currentScroll = window.scrollY;

      if (event.deltaY > 0) {
        // Scroll down
        const nextSection = Math.min(Math.floor(currentScroll / scrollDistance) + 1, sections.length - 1);
        window.scrollTo({
          top: sections[nextSection].offsetTop,
          behavior: 'smooth'
        });
      } else {
        // Scroll up
        const prevSection = Math.max(Math.floor(currentScroll / scrollDistance) - 1, 0);
        window.scrollTo({
          top: sections[prevSection].offsetTop,
          behavior: 'smooth'
        });
      }
    };

    window.addEventListener('wheel', handleScroll, { passive: false });

    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<MainPage />} />
        <Route path="/project/:id" element={<ProjectDetailPage />} />
      </Routes>
    </Router>
  );
};

const MainPage = () => {
  return (
    <>
      <Hero />
      <ExperienceSection />
      <Projects />
      <ContactForm />
    </>
  );
};

const ProjectDetailPage = () => {
  return (
    <>
      <ProjectDetail />
    </>
  );
};

export default App;
