import React, { useRef } from 'react';
import styled from 'styled-components';

const Header = () => {
  const projectsRef = useRef(null);
  const experienceRef = useRef(null);
  const contactRef = useRef(null);

  const scrollToRef = (ref) => {
    ref.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  return (
    <Nav>
      <LogoContainer onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}>
        Filip Veskrna
      </LogoContainer>
      <NavMenu>
        <NavItem onClick={() => scrollToRef(projectsRef)}>Projects</NavItem>
        <NavItem onClick={() => scrollToRef(experienceRef)}>Experience</NavItem>
        <NavItem onClick={() => scrollToRef(contactRef)}>Contact</NavItem>
      </NavMenu>
      <DownloadButton href="/path-to-resume.pdf" download>
        Download Resume
      </DownloadButton>
      <div ref={projectsRef}></div>
      <div ref={experienceRef}></div>
      <div ref={contactRef}></div>
    </Nav>
  );
};

const Nav = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  color: #fff;
  position: sticky;
  top: 0;
  width: 100%;
  z-index: 1000;
  background-color: #333; /* Adjust background color as needed */
`;

const LogoContainer = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  cursor: pointer;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
`;

const NavItem = styled.div`
  margin-left: 2rem; /* Adjust spacing between menu items */
  cursor: pointer;
`;

const DownloadButton = styled.a`
  background-color: #e94560; /* Button background color */
  color: #fff; /* Button text color */
  padding: 0.5rem 1rem;
  border-radius: 4px;
  text-decoration: none;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #c92a44; /* Darker shade of the button color on hover */
  }
`;

export default Header;
